import React, { useEffect, useState } from "react";
import { ApolloClient, InMemoryCache, ApolloProvider, useQuery, gql } from "@apollo/client";
import { Card, CardContent } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import { motion } from "framer-motion";

const client = new ApolloClient({
  uri: "http://localhost:4000/graphql",
  cache: new InMemoryCache(),
});

const GET_GAMES = gql`
  query GetGames($search: String) {
    games(search: $search) {
      id
      title
      coverImage
      platform
      rating
      review
    }
  }
`;

function GameCard({ game }) {
  return (
    <motion.div whileHover={{ scale: 1.03 }} className="w-full max-w-md">
      <Card className="rounded-2xl shadow-md overflow-hidden">
        <CardContent className="flex gap-4 p-4">
          <img src={game.coverImage} alt={game.title} className="w-24 h-32 object-cover rounded-lg" />
          <div className="flex flex-col">
            <h2 className="text-xl font-bold">{game.title}</h2>
            <p className="text-sm text-gray-500">{game.platform}</p>
            <p className="text-yellow-600">Rating: {game.rating}/10</p>
            <p className="text-sm mt-2">{game.review}</p>
          </div>
        </CardContent>
      </Card>
    </motion.div>
  );
}

function GameList() {
  const [searchTerm, setSearchTerm] = useState("");
  const [searchQuery, setSearchQuery] = useState("");

  const { loading, error, data, refetch } = useQuery(GET_GAMES, {
    variables: { search: searchQuery },
  });

  useEffect(() => {
    refetch({ search: searchQuery });
  }, [searchQuery, refetch]);

  return (
    <div className="flex flex-col items-center p-4 space-y-6">
      <div className="flex gap-2 w-full max-w-md">
        <Input
          placeholder="Search games..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <Button onClick={() => setSearchQuery(searchTerm)}>Search</Button>
      </div>

      {loading && <p>Loading games...</p>}
      {error && <p>Error loading games.</p>}

      <div className="grid grid-cols-1 gap-4">
        {data?.games.map((game) => (
          <GameCard key={game.id} game={game} />
        ))}
      </div>
    </div>
  );
}

export default function App() {
  return (
    <ApolloProvider client={client}>
      <main className="min-h-screen bg-gray-100">
        <h1 className="text-3xl font-bold text-center pt-8 pb-4">Gameboxd</h1>
        <GameList />
      </main>
    </ApolloProvider>
  );
}
